    &lt;html&gt; &lt;head&gt; &lt;/head&gt; &lt;body&gt; &lt;p&gt; ;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;; &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; File:&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;edits.lsp &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; Project:&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;You are coming into us who cannot withstand you &lt;/p&gt; &lt;p&gt; ;;;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;for Ensemble Aventure, Freiburg &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; Purpose:&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;'By hand' edits for score. &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; Author:&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;Michael Edwards: m@michael-edwards.org &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; Creation date:&amp;#160;&amp;#160;&amp;#160;&amp;#160;13th February 2011 (Ko Libong, Thailand) &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;; $$ Last modified: 09:52:21 Sat Jan 14 2012 ICT &lt;/p&gt; &lt;p&gt; ;;; &lt;/p&gt; &lt;p&gt; ;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;; &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (in-package :sc) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (proclaim '(special +coming-rthm-chain+)) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defparameter +coming-tutti+ (players +coming-rthm-chain+)) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; ;; note is 1-based and counts tied notes but not rests of course. &lt;/p&gt; &lt;p&gt; ;; ins could be a list of instruments &lt;/p&gt; &lt;p&gt; (defun acmtn (ins bar note &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless (listp ins) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(setf ins (list ins))) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(loop for i in ins do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-marks-to-note +coming-rthm-chain+ bar note i marks))) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defun acmte (ins bar event mark) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless (listp ins) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(setf ins (list ins))) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(loop for i in ins do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-mark-to-event +coming-rthm-chain+ bar event i mark))) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defun acmtnb (ins bar note mark) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless (listp ins) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(setf ins (list ins))) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(loop for i in ins do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-mark-before-note +coming-rthm-chain+ bar note i mark))) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defmacro amstr (bar note &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;`(acmtn '(violin cello viola) ,bar ,note ,@marks)) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defmacro amstr2 (bar note &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;`(acmtn '(violin cello) ,bar ,note ,@marks)) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defmacro acmtns (ins start end before &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless marks &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(error &amp;quot;no marks: ~a ~a&amp;quot; ins start)) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;;; `(unless (listp ,ins) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;;; (setf ,ins (list ,ins))) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;`(loop for i in (if (listp ,ins) ,ins (list ,ins)) do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-marks-to-notes +coming-rthm-chain+ ,start ,end i ,before &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;,@marks))) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defmacro amsstr (start end before &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless marks &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(error &amp;quot;no marks: strings ~a&amp;quot; start)) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;`(loop for ins in '(violin cello viola) do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-marks-to-notes +coming-rthm-chain+ ,start ,end ins ,before &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;,@marks))) &lt;/p&gt; &lt;p&gt; &lt;/p&gt; &lt;p&gt; (defmacro amsstr2 (start end before &amp;amp;rest marks) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;(unless marks &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;(error &amp;quot;no marks: vln/vc: ~a&amp;quot; start)) &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;`(loop for ins in '(violin cello) do &lt;/p&gt; &lt;p&gt; &amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;&amp;#160;(add-marks-to-notes +coming-rthm-chain+ ,start ,en